(function(){
	
	var access = workflow.scratchpad.accessRecord;
	
	workflow.scratchpad.delivery_group = access.desktop_group;
	workflow.scratchpad.site_id = access.site_id;
	workflow.scratchpad.users = '';
	var users = access.users;
	
	var temp = [];
	for(var i = 0; i < users.length; i++){
		temp.push(users[i].sys_id);
	}

	workflow.scratchpad.users = temp.join();
})();

