(function(){
	
	var record = workflow.scratchpad.accessRecord;
	
	var users = record.users;
	
	var userUtil = new CTXUserTools();
	
	for(var i = 0; i < users.length; i++){
		users[i].ldap_server = userUtil.getUserLDAPServer(users[i].sys_id);
		users[i].user_name = userUtil.getUserName(users[i].sys_id);
	}
	
	workflow.scratchpad.adgrp_users = users;
})();

